function bindScroll(){$(window).on("scroll",function(){const t=$(".animate");for(let n=0;n<t.length;n++){let o=t[n];if(!o.hasAttribute("data-animate"))return;if($(window).height()+$(window).scrollTop()>$(o).offset().top+.5*$(o).height()){const t=$(o).attr("data-animate");$(o).addClass(t)}}})}$(document).ready(()=>{bindScroll()});